import type { GetStaticProps, NextPage } from "next";
import Head from "next/head";
import {Image} from '@chakra-ui/react';
import { useEffect } from "react";
import styles from "../styles/Home.module.css";
import { api } from "../services/api";
import { Button } from "@chakra-ui/react";
import {BsCart4} from 'react-icons/bs'

type Product = {
    name: string;
    id: string;
    description: string;
    price: number;
    image: string;
};

type PropTypes = {
    products: Product[];
};

const Home = ({ products }: PropTypes) => {
    
    function handleBuy(productName: string, productPrice: number) {
        const productToBuy = {
            productName,
            productPrice: productPrice.toLocaleString('pt-BR', {style: 'currency', currency: 'BRL'})
        }

        let mensagem = `Olá! Eu gostaria muito de comprar esse produto. Produto: ${productName} Preço: ${productToBuy.productPrice}\n\n`;
        window.open(`https://api.whatsapp.com/send?phone=5584988602905&text=${mensagem}`);
    }

    return (
        <div className={styles.container}>
            <Head>
                <title>Catálogo Amiamor</title>
                <meta
                    name="description"
                    content="Catálogo online de produtos"
                />
                <link rel="icon" href="/favicon.ico" />
            </Head>

            <main className={styles.main}>
                <h1 className={styles.title}>
                    Bem vindo ao <a>AmiAmor!</a>
                </h1>

                <p className={styles.description}>
                    Encomende já o seu produto conosco.
                </p>

                <div className={styles.grid}>
                    {products.map((product) => {
                        return (
                            <a
                                key={product.id}
                                className={styles.card}
                            >
                                <Image
                                    boxSize="100%"
                                    height="150px"
                                    objectFit="cover"
                                    src={product.image}
                                    alt={product.name}
                                    className={styles.container}
                                />
                                <h3 key={product.id}>{product.name}</h3>
                                <span>{product.description}</span>
                                <p>{product.price.toLocaleString('pt-BR', {style: 'currency', currency: 'BRL'})}</p>
                                <Button
                                    leftIcon={<BsCart4 color="white" />}
                                    colorScheme="blue"
                                    size={'sm'}
                                    onClick={() => handleBuy(product.name, product.price)}
                                >
                                    Comprar
                                </Button>
                            </a>
                        );
                    })}
                </div>
            </main>

            <footer className={styles.footer}>
                <span className={styles.logo}>
                    <Image
                        src={
                            "data:image/jpeg;base64,"
                        }
                        alt="logo"
                        width={50}
                        height={50}
                    />
                </span>
            </footer>
        </div>
    );
};

export default Home;

export const getStaticProps: GetStaticProps = async () => {
    const result = await api.get("/products");
    const productList = result.data.map((item: any) => {
        return {
            ...item.data,
            id: item.ref["@ref"].id,
        };
    });
    return {
        props: {
            products: productList,
        },
    };
};
